import React, { Component } from 'react'

class Listing extends Component {
  // use this.props.index to call a new function then pass on the props to pass index back to MapContainer
  // click for the marker at that index position in the list of markers at this.state.markers
  // take the code you already have in the marker click listener and move it into another function that take the index as a parameter

  toggleSelectedListing = (mapProps, marker) => {
    // set google to mapProps from Map for marker creation
    const { google } = mapProps;
    const infoWindow = new google.maps.InfoWindow();

    // if there is an open infoWindow, and another is clicked, 
    // close the active marker and set activeMarker state to null. References:
    // https://developers.google.com/maps/documentation/javascript/events
    // https://developer.mozilla.org/en-US/docs/Web/Guide/Events/Creating_and_triggering_events
    // https://stackoverflow.com/questions/34053826/how-to-use-this-in-google-maps-event-addlistener
    google.maps.event.addListener(infoWindow, 'closeclick', () => {
      this.state.activeMarker.setAnimation(null);
      this.setState({ activeMarker: null });
    });

    this.props.index.addListener('click', () => {
      // Use slice to create a temporary marker array that can be iterated over to stop the animation for the markers
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/slice
      let tempMarkers = this.state.markers.slice();
      tempMarkers.forEach(mark => {
        mark.setAnimation(null);
      })
      //set the content for the new window
      infoWindow.setContent(windowContent);
      //show the new window based on the clicked marker
      infoWindow.open(map, marker);
      //set state to activeMarker
      this.setState({ activeMarker: marker });
      //set marker animation
      marker.setAnimation(google.maps.Animation.BOUNCE);
    });
  }

  render() {
    let displayedImage = (this.props.listing.image_url !== "") ?
      this.props.listing.image_url : 'http://via.placeholder.com/250x250.jpg/FFFFFF/000000/?text=No+Image+Available';

    return (
      <section onClick={this.props.toggleSelectedListing()} className='listing' marker={this.props.activeMarker}>
        <div tabIndex='0' className="listingName">{this.props.listing.name}</div>
        <div className="listingPhoto">
          <img alt={this.props.listing.name + " photo"} src={displayedImage} />
        </div>
        <div>{this.props.listing.address[0]}</div>
        <div>{this.props.listing.address[1]}</div>
        <div><a href={this.props.listing.url}>See Yelp For More Information</a></div>
      </section>
    )
  }
}

export default Listing
