import React, {Component} from 'react';
import { Map, Marker, InfoWindow, GoogleApiWrapper } from 'google-maps-react';

class MapContainer extends Component {
  state = {
    markers: [],
    activeMarker: null,
    showInfo: false
  }

  componentDidMount() {}

  loadMarkers = () => {
    let markers = this.props.yelpData.map((item) => {
      return (
        <Marker
          key={item.id}
          position={item.coordinates}
          name={item.name}
          onClick={this.onMarkerClick}
          />
        );
      })
    this.setState({ markers });
  };

  onMarkerClick = (marker) => {
    if (marker === this.state.activeMarker) {
      this.setState({ activeMarker: marker, showInfo: true })
    } else {
      this.setState({ activeMarker: null, showInfo: false })
    }
  };

  onInfoWindowClose = () => {
    this.setState({ showInfo: false, activeMarker: null, activeInfo: null })
  };

  render() {
    const center = {
      lat: 32.322613,
      lng: -95.262592
    }

  // Display Map
    return (
      <Map 
        aria-label="map"
        role="application"
        google={this.props.google}
        zoom={13}        
        initialCenter={center}
        onReady={this.loadMarkers}
        >
      </Map>
    )
  }
}

export default GoogleApiWrapper({
  apiKey: ("AIzaSyCxXK6lMDoTo4dHosssdE0SyJ8UtVOtpbU")
})(MapContainer)
