import React, { Component } from 'react';
import ListMenu from './src/components/ListMenu';
import MapContainer from './src/components/MapContainer';
import locations from './src/data/locations.json';
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import './App.css';

library.add(faBars)

const YELP_KEY = 'nURSXAKqkUMPdntGky6KItOf0vSFaLnwcaN-w7MPeI5543g1OtE6dVSA_tXWRMwZaSUzNuzeyGIfT_gsINuzE_9_HO8B__a3-cvcVUNrWgOLH2yX0FvC8q3ECcnXW3Yx'
const YELP_CLI_ID = 'MERJI2mNZ3vK030FjnC23Q'

class App extends Component {
  state = {
    locations: locations,
    yelpData: [],
    open: false
  }

  styles = {
    button: {
      position: "absolute",
      left: 10,
      top: 5,
      padding: 10,
      background: "#c6b9cd"
    }
  }

  // Step 1: Get location information from Yelp
  getYelpInfo = () => {
    let url = "https://api.yelp.com/v3/businesses/search?radius=1000&latitude=32.322613&longitude=-95.262592"
    let headers = new Headers({
        Authorization: `Bearer ${YELP_KEY}`
      });
    let request = new Request(url, {
      method: 'GET',
      headers
    })
    fetch(request).then(response => response.json()).then(console.log(response))
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h2>Nom-Nom Finder</h2>
          <button style={this.styles.button} onClick={this.toggleMenu}><FontAwesomeIcon icon="bars"/></button>
        </header>
        <ListMenu
          yelpData={this.state.yelpData}
          getYelpInfo={this.getYelpInfo}
          open={this.state.open} />
        <MapContainer
          locations={this.state.locations}
          yelpData={this.state.yelpData}
          getYelpInfo={this.getYelpInfo} />
      </div>

      // TODO: Create a full-screen map that displays markers for restaurants
      // near the UT Tyler campus. 
    );
  }
}

export default App;
